mapper.select:
SELECT concert_date_idx 
FROM concert_date 
WHERE SUBSTRING(concert_date_date, 1, 10) = #{concert_date} 
AND concert_idx = #{concert_idx};

SELECT concert_date_idx
FROM concert_date
WHERE concert_date_date = #{concertDate}

SELECT concert_date_idx
FROM concert_date
WHERE concert_idx = #{concert_idx}
AND SUBSTRING(concert_date_date, 1, 10) = #{concert_date}

choose.when:
UPDATE s_hall
SET s_hall_a = 1
WHERE concert_date_idx = (
    SELECT concert_date_idx
    FROM concert_date
    WHERE SUBSTRING(concert_date_date, 1, 10) = #{concertDate}
)
AND seat_idx = #{seatIdx}
AND s_hall_row_no = #{row}

